let print_builtin_inline oc name args res =  fprintf oc "%s begin %s\n" comment name;  let n = match name, args, res with  (* Integer arithmetic *)  | ("__builtin_bswap" | "__builtin_bswap32"), [IR a1], [IR res] ->      fprintf oc "	rev	%a, %a\n" ireg res ireg a1; 1  ... 
  (* Synchronization *)  | "__builtin_barrier", [], _ ->	  (* compiler barrier, prevents CompCert to reorder across barrier *)      fprintf oc "	@ compiler barrier\n"; 0   | "__builtin_barrier_dsb", [], _ ->      (* arm data barrier *)      fprintf oc "	dsb     @ data barrier\n"; 1  | "__builtin_barrier_isb", [], _ ->      (* arm instruction barrier *)      fprintf oc "	isb     @ instruction barrier\n"; 1  (* Catch-all *)  | _ ->      invalid_arg ("unrecognized builtin " ^ name)  in  fprintf oc "%s end %s\n" comment name;  n